mapper.select:
SELECT id, user_id, folder_name, folder_description, collect_count, create_time, update_time
FROM code_pen_folder
WHERE id = #{id}

mapper.update:
UPDATE code_pen_folder
folder_name = #{folderName}, folder_description = #{folderDescription}, update_time = NOW()
WHERE id = #{id}

UPDATE code_pen_folder
SET collect_count = collect_count + #{increment}
WHERE id = #{id}
