mapper.insert:
INSERT INTO code_pen_fork_transaction (
    original_pen_id, forked_pen_id, original_author_id, fork_user_id, fork_price, transaction_type, create_time
) VALUES (
    #{originalPenId}, #{forkedPenId}, #{originalAuthorId}, #{forkUserId}, #{forkPrice}, #{transactionType}, NOW()
)

mapper.select:
SELECT id, original_pen_id, forked_pen_id, original_author_id, fork_user_id, fork_price, transaction_type, create_time
FROM code_pen_fork_transaction
WHERE id = #{id}
