mapper.select:
SELECT id, tag_name, tag_description, use_count, create_time, update_time
FROM code_pen_tag
WHERE id = #{id}

SELECT id, tag_name, tag_description, use_count, create_time, update_time
FROM code_pen_tag
WHERE tag_name = #{tagName}

mapper.update:
UPDATE code_pen_tag
tag_name = #{tagName}, tag_description = #{tagDescription}, update_time = NOW()
WHERE id = #{id}

UPDATE code_pen_tag
SET use_count = use_count + 1
WHERE id = #{id}

UPDATE code_pen_tag
SET use_count = use_count + (SELECT use_count FROM code_pen_tag WHERE id = #{sourceId})
WHERE id = #{targetId}
