mapper.update:
UPDATE community_post
title = #{title}, content = #{content}, category_id = #{categoryId}, view_count = #{viewCount}, like_count = #{likeCount}, comment_count = #{commentCount}, collect_count = #{collectCount}, is_top = #{isTop}, top_expire_time = #{topExpireTime}, status = #{status}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM community_post


        AND status = #{status}


        AND author_name LIKE CONCAT('%', #{authorName}, '%')


        AND category_id = #{categoryId}


        AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}

SELECT 
FROM community_post p
LEFT JOIN community_category cc ON p.category_id = cc.id


        AND p.status = #{request.status}


        AND p.author_name LIKE CONCAT('%', #{request.authorName}, '%')


        AND p.category_id = #{request.categoryId}


        AND (p.title LIKE CONCAT('%', #{request.keyword}, '%') OR p.content LIKE CONCAT('%', #{request.keyword}, '%'))


        AND p.create_time >= #{request.startTime}


        AND p.create_time <= #{request.endTime}


ORDER BY p.is_top DESC, p.create_time DESC

mapper.update:
UPDATE community_post 
SET is_top = 1, top_expire_time = #{topExpireTime}, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET is_top = 0, top_expire_time = NULL, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET status = 2, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET status = 3, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET view_count = view_count + 1, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET like_count = like_count + #{count}, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET collect_count = collect_count + #{count}, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET comment_count = comment_count + #{count}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(DISTINCT p.id) FROM community_post p
INNER JOIN community_post_tag pt ON p.id = pt.post_id
AND p.status = 1
AND p.category_id = #{request.categoryId}
AND pt.tag_id = #{request.tagId}
AND MATCH(p.title, p.content) AGAINST(#{request.keyword} IN NATURAL LANGUAGE MODE)

SELECT ,
(p.like_count * 3 + p.comment_count * 5 + p.collect_count * 8 + p.view_count * 0.1) as hot_score
FROM community_post p
LEFT JOIN community_category cc ON p.category_id = cc.id
WHERE p.status = 1
AND p.create_time >= #{startTime}
AND (p.like_count * 3 + p.comment_count * 5 + p.collect_count * 8 + p.view_count * 0.1) >= #{minScore}
ORDER BY hot_score DESC

    LIMIT #{limit}
