mapper.update:
UPDATE flashcard
front_content = #{frontContent}, back_content = #{backContent}, status = #{status}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
SELECT f.*, d.name as deck_name
FROM flashcard f
LEFT JOIN flashcard_deck d ON f.deck_id = d.id
WHERE f.status = 1
AND NOT EXISTS (
    SELECT 1 FROM flashcard_study_record r
    WHERE r.card_id = f.id AND r.user_id = #{userId}
)

    AND f.deck_id = #{deckId}

ORDER BY f.create_time ASC

    LIMIT #{limit}
