mapper.update:
UPDATE flashcard_study_record
ease_factor = #{easeFactor}, interval_days = #{intervalDays}, repetitions = #{repetitions}, quality = #{quality}, next_review_date = #{nextReviewDate}, last_review_time = #{lastReviewTime}, total_reviews = #{totalReviews}, correct_count = #{correctCount}, wrong_count = #{wrongCount}, status = #{status}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
SELECT 
FROM flashcard_study_record
WHERE user_id = #{userId} AND card_id = #{cardId}

SELECT 
FROM flashcard_study_record
WHERE user_id = #{userId}
AND next_review_date <= #{date}
AND status != 2

    AND deck_id = #{deckId}

ORDER BY next_review_date ASC

    LIMIT #{limit}

SELECT COUNT(*)
FROM flashcard_study_record
WHERE user_id = #{userId}
AND next_review_date <= #{date}
AND status != 2

SELECT COUNT(*)
FROM flashcard_study_record
WHERE user_id = #{userId} AND deck_id = #{deckId} AND status = #{status}

SELECT COALESCE(SUM(total_reviews), 0)
FROM flashcard_study_record
WHERE user_id = #{userId}

SELECT COUNT(*)
FROM flashcard_study_record
WHERE user_id = #{userId}
AND deck_id = #{deckId}
AND next_review_date <= #{date}
AND status != 2

SELECT COUNT(*)
FROM flashcard_study_record
WHERE user_id = #{userId}
AND deck_id = #{deckId}
