mapper.select:
SELECT 
FROM flashcard_daily_stats
WHERE user_id = #{userId} AND stat_date = #{statDate}

SELECT 
FROM flashcard_daily_stats
WHERE user_id = #{userId}
AND stat_date BETWEEN #{startDate} AND #{endDate}
ORDER BY stat_date ASC

mapper.insert:
INSERT INTO flashcard_daily_stats (
    user_id, stat_date, new_cards, review_cards, correct_cards, study_duration, create_time, update_time
) VALUES (
    #{userId}, #{statDate}, #{newCards}, #{reviewCards}, #{correctCards}, #{studyDuration}, #{createTime}, #{updateTime}
)

mapper.select:
SELECT COUNT(DISTINCT stat_date)
FROM flashcard_daily_stats
WHERE user_id = #{userId}


SELECT stat_date
FROM flashcard_daily_stats
WHERE user_id = #{userId} AND stat_date <= #{endDate}
ORDER BY stat_date DESC
LIMIT #{limit}
