mapper.update:
UPDATE knowledge_map 
SET status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE knowledge_map 
SET view_count = view_count + 1 
WHERE id = #{id}

UPDATE knowledge_map 
SET node_count = #{nodeCount}, update_time = NOW()
WHERE id = #{mapId}

mapper.delete:
DELETE FROM knowledge_map 
WHERE id IN

    #{id}
