mapper.update:
UPDATE moment_comments
content = #{content}, status = #{status},
WHERE id = #{id}

mapper.select:
SELECT 
FROM moment_comments


        status = #{status}


        status = 1


        AND moment_id = #{momentId}


        AND user_id = #{userId}


        AND user_id IN (SELECT id FROM user_info WHERE nickname LIKE CONCAT('%', #{userNickname}, '%'))


        AND content LIKE #{contentKeyword}


        AND create_time >= #{startDate}


        AND create_time <= #{endDate}


ORDER BY create_time DESC

    LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM moment_comments


        status = #{status}


        status = 1


        AND moment_id = #{momentId}


        AND user_id = #{userId}


        AND user_id IN (SELECT id FROM user_info WHERE nickname LIKE CONCAT('%', #{userNickname}, '%'))


        AND content LIKE #{contentKeyword}


        AND create_time >= #{startDate}


        AND create_time <= #{endDate}
