mapper.update:
UPDATE moments
content = #{content}, images = #{images}, status = #{status},
WHERE id = #{id}

mapper.select:
SELECT 
FROM moments


        status = #{status}


        status = 1


        AND user_id = #{userId}


        AND user_id IN (SELECT id FROM user_info WHERE nickname LIKE CONCAT('%', #{userNickname}, '%'))


        AND create_time >= #{startDate}


        AND create_time <= #{endDate}


ORDER BY create_time DESC

    LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM moments


        status = #{status}


        status = 1


        AND user_id = #{userId}


        AND user_id IN (SELECT id FROM user_info WHERE nickname LIKE CONCAT('%', #{userNickname}, '%'))


        AND create_time >= #{startDate}


        AND create_time <= #{endDate}

SELECT COALESCE(SUM(m.like_count), 0)
FROM moments m
WHERE m.status = 1

    AND m.create_time >= #{startDate}


    AND m.create_time <= #{endDate}

SELECT COUNT(DISTINCT m.user_id)
FROM moments m
WHERE m.status = 1

    AND m.create_time >= #{startDate}


    AND m.create_time <= #{endDate}

SELECT FROM moments WHERE id IN
#{id}
AND status = 1 ORDER BY create_time DESC
