mapper.select:
SELECT

FROM daily_content
WHERE content_type = #{contentType}

  AND status = #{status}

ORDER BY create_time DESC

    LIMIT #{limit}

SELECT

FROM daily_content
WHERE programming_language = #{programmingLanguage}
  AND status = #{status}
ORDER BY create_time DESC

    LIMIT #{limit}

SELECT

FROM daily_content
WHERE difficulty_level = #{difficultyLevel}
  AND status = #{status}
ORDER BY create_time DESC

    LIMIT #{limit}

SELECT

FROM daily_content
WHERE content_type = #{contentType}
  AND status = #{status}
ORDER BY RAND()

    LIMIT #{limit}

SELECT

FROM daily_content
WHERE status = #{status}

    AND content_type IN

        #{item}



    AND (programming_language IS NULL OR programming_language IN

        #{item}

    )


    AND (difficulty_level IS NULL OR difficulty_level <= #{difficultyLevel})

ORDER BY create_time DESC

    LIMIT #{limit}

SELECT

FROM daily_content
WHERE status = #{status}
ORDER BY view_count DESC, like_count DESC, create_time DESC

    LIMIT #{limit}

mapper.insert:
INSERT INTO daily_content (
    content_type, title, content, author, programming_language, tags,
    difficulty_level, source_url, view_count, like_count, status
) VALUES (
    #{contentType}, #{title}, #{content}, #{author}, #{programmingLanguage}, #{tags},
    #{difficultyLevel}, #{sourceUrl}, #{viewCount}, #{likeCount}, #{status}
)

mapper.select:
SELECT
FROM daily_content WHERE id IN
#{id}
