mapper.insert:
INSERT INTO resume_analytics (
    resume_id, view_count, export_count, share_count,
    unique_visitors, last_access_time, create_time, update_time
) VALUES (
    #{resumeId}, #{viewCount}, #{exportCount}, #{shareCount},
    #{uniqueVisitors}, #{lastAccessTime}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE resume_analytics
SET last_access_time = #{lastAccessTime},
    update_time = #{lastAccessTime}
WHERE resume_id = #{resumeId}
