mapper.insert:
INSERT INTO resume_info (
    user_id, resume_name, template_id, summary,
    status, visibility, version, create_time, update_time
) VALUES (
    #{userId}, #{resumeName}, #{templateId}, #{summary},
    #{status}, #{visibility}, #{version}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE resume_info
resume_name = #{resumeName}, template_id = #{templateId}, summary = #{summary}, status = #{status}, visibility = #{visibility}, version = #{version}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
SELECT  FROM resume_info

    user_id = #{userId}

        AND resume_name LIKE CONCAT('%', #{request.keyword}, '%')


        AND status = #{request.status}


ORDER BY update_time DESC
