mapper.insert:
INSERT INTO resume_shares (
    resume_id, share_code, share_url, status, access_count,
    expire_time, create_time, update_time
) VALUES (
    #{resumeId}, #{shareCode}, #{shareUrl}, #{status}, #{accessCount},
    #{expireTime}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE resume_shares
share_code = #{shareCode}, share_url = #{shareUrl}, status = #{status}, access_count = #{accessCount}, expire_time = #{expireTime}, update_time = #{updateTime},
WHERE id = #{id}
