mapper.select:
SELECT id, user_id, stat_date, violation_count, last_violation_time, 
       is_restricted, restrict_end_time
FROM sensitive_user_violation
WHERE user_id = #{userId}
  AND stat_date = #{statDate}
LIMIT 1

mapper.insert:
INSERT INTO sensitive_user_violation (
    user_id, stat_date, violation_count, last_violation_time, 
    is_restricted, restrict_end_time
) VALUES (
    #{userId}, #{statDate}, #{violationCount}, #{lastViolationTime}, 
    #{isRestricted}, #{restrictEndTime}
)

mapper.update:
UPDATE sensitive_user_violation
violation_count = #{violationCount}, last_violation_time = #{lastViolationTime}, is_restricted = #{isRestricted}, restrict_end_time = #{restrictEndTime},
WHERE id = #{id}

mapper.select:
SELECT user_id, SUM(violation_count) as violation_count, 
       MAX(last_violation_time) as last_violation_time,
       MAX(is_restricted) as is_restricted
FROM sensitive_user_violation


        AND stat_date >= #{startDate}


        AND stat_date <= #{endDate}


GROUP BY user_id
ORDER BY violation_count DESC

    LIMIT #{limit}
