mapper.select:
SELECT t.id, t.team_id, t.task_name, t.task_desc, t.task_type, t.target_value, t.target_unit, t.repeat_type, t.repeat_days, t.require_content, t.require_image, t.start_date, t.end_date, t.status, t.create_time, t.create_by FROM study_team_task t WHERE t.team_id = #{teamId} AND t.is_deleted = 0
AND t.status = #{status}
ORDER BY t.create_time DESC

SELECT COUNT(DISTINCT user_id)
FROM study_team_checkin
WHERE task_id = #{taskId}
AND checkin_date = #{today}
AND is_deleted = 0

SELECT COUNT(1)
FROM study_team_checkin
WHERE task_id = #{taskId}
AND user_id = #{userId}
AND checkin_date = #{today}
AND is_deleted = 0
